for _, frameCollection in pairs(frames) do
    for i = 1, #frameCollection do
        frameCollection[i] = frameCollection[i]:gsub("e", "\27")
    end
end

local function sleep()
    os.execute("sleep " .. 1 / FPS)
end

local function drawFrame(frame)
    io.write(string.format("\27[%dA", CANVAS_HEIGHT))
    io.flush()

    io.write("\r" .. frame)
    io.flush()
end

while true do
    for i = 1, #frames.sleepingCat do
        drawFrame(frames.sleepingCat[i])
        sleep()
    end
end
